import { EmailListModalComponent } from './../email-list-modal/email-list-modal.component';
import { IResponse } from 'src/app/models/response.interface';
import { JdModalComponent } from '../jd-modal/jd-modal.component';
import { Component, OnInit, EventEmitter, Output, Input} from '@angular/core';
import { AppServicesService } from 'src/app/services/app-services.service';
import { NgbModal, NgbModalRef, ModalDismissReasons} from '@ng-bootstrap/ng-bootstrap';
import { Router, ActivatedRoute, Params } from '@angular/router';
import { ModalComponent } from 'src/app/reusable-components/modal/modal.component';
import { HttpErrorResponse } from '@angular/common/http';
import html2canvas from 'html2canvas';  
import{JobService} from '../services/job.service'
import * as jsPDF from 'jspdf'

@Component({
  selector: 'app-jd-list',
  templateUrl: './jd-list.component.html',
  styleUrls: ['./jd-list.component.scss'],
})

export class JdListComponent implements OnInit {
  jobsList: any;
  jdObject: any;
  pager: any;
  obj:any;
  searchJd:any;
  constructor(private _service: AppServicesService, private router: Router,
              private modalService: NgbModal,private jobService:JobService) {}
  imageUrl=
  "data:image/png;base64,"
  ngOnInit() {
    this.loadJds();
  }

  loadJds() {
    return this.jobService.getAllJobs().subscribe((response: any) => {
      this.jobsList = response.payload.data;
  

    });
  }

  jdUpdateModal(id: string) {
    const modalRef: NgbModalRef = this.modalService.open(JdModalComponent)
    modalRef.componentInstance.jdUpdateId = id;
    modalRef.componentInstance.closeModal.subscribe((rerender: boolean) => {
      this.loadJds();
      modalRef.close();
    });
   
  }

  sendBulkEmail(id) {
    const modalRef: NgbModalRef = this.modalService.open(EmailListModalComponent);
    modalRef.componentInstance.jdObjId = id;
    modalRef.componentInstance.closeModal.subscribe((rerender: boolean) => {
      modalRef.close();
    });
  }

  deleteJd(id: string) {
    const modalRef: NgbModalRef = this.modalService.open(ModalComponent);

    modalRef.componentInstance.shouldConfirm = true;

    modalRef.componentInstance.closeModal.subscribe((rerender: boolean) => {
      modalRef.close();
    });
    modalRef.componentInstance.emitPerformRequest.subscribe(() => {
      this.jobService.deleteJd(id).subscribe((res: any) => {
        this.loadJds();
        modalRef.componentInstance.success = res.success;
        modalRef.componentInstance.message = res.payload.message;
        }, (error: HttpErrorResponse) => {
          modalRef.componentInstance.success = error.ok;
          modalRef.componentInstance.message = error.error.payload.message;
    });
  });
  }

  downloadPdf(id) {
    this.jobService.getJdData(id).subscribe((res: any) => {
      if (res.success) {
        this.jdObject = res.payload.data;
      }
    });
   setTimeout(()=>{
      this.convertToPDF();
       },5000
      )  
    }
  convertToPDF() {
      var data = document.getElementById('content');
      data.style.display="block";
      html2canvas(data, { allowTaint: true }).then(canvas => {
         let HTML_Width = canvas.width;
         let HTML_Height = canvas.height;
         let top_left_margin = 15;
         let PDF_Width = HTML_Width*0.45 + (top_left_margin * 2);
         let PDF_Height = (PDF_Width * 1.6 ) + (top_left_margin * 2);
         let canvas_image_width = HTML_Width;
         let canvas_image_height = HTML_Height;
         let totalPDFPages = Math.ceil(HTML_Height / PDF_Height) - 1;
         canvas.getContext('2d');
         let imgData = canvas.toDataURL("image/png",1.0);
         let pdf = new jsPDF('p', 'pt', 'a4');
         pdf.addImage(imgData, 'PNG', top_left_margin, top_left_margin, canvas_image_width*0.79,
                      canvas_image_height*0.90);
       
         for (let i = 1; i <= totalPDFPages; i++) {
            pdf.addPage('a4', 'p');
            pdf.setPage(i+1);
            pdf.text("cyg", 400,1200 );
            pdf.addImage(imgData, 'PNG',top_left_margin, -(PDF_Height * i) + (top_left_margin * 4)  ,
                         canvas_image_width*0.79, canvas_image_height*0.90);
          }
          pdf.save("jobdescription"+this.jdObject.id+'.pdf');
          var fileEncode = btoa(pdf.output());
          this.jobService.updateJobInfo({pdfString:fileEncode},Number(this.jdObject.id)).subscribe((res: any) => {
                 
  
            });
        });
    
        data.style.display="none";
   }
  

  datecheck(closingDate) {
    const currentDate = new Date().toISOString();
    if (closingDate <= currentDate) {
      return 1;
    } else { return 0; }
  }
}
